var dungeon_crawler = dungeon_crawler || {};
dungeon_crawler.core = {}
//D:/Documents/Projects/Vs/BlazorDungeonCrawler/BlazorDungeonCrawler/UncompiledScripts/dungeon_crawler/classes/dungeon_crawler.core.globals.js
dungeon_crawler.core.globals = {
    stageRows: 10,
    stageCols: 10,

    hexHeight: 90,
    hexWidth: 100
}; 
//D:/Documents/Projects/Vs/BlazorDungeonCrawler/BlazorDungeonCrawler/UncompiledScripts/dungeon_crawler/pages/dungeon_crawler.main.js
dungeon_crawler.main = {
    startup() {
        let hexagonHeight = dungeon_crawler.core.globals.hexHeight;
        let hexagonWidth = dungeon_crawler.core.globals.hexWidth;

        let stageCols = dungeon_crawler.core.globals.stageCols;
        let stageRows = dungeon_crawler.core.globals.stageRows;    

        //set stage dimentions
        let stageHeight = stageCols * hexagonHeight;
        let stageWidth = stageRows * hexagonWidth;

        $('#stage').css({ 'height': `${stageHeight}px`, 'width': `${stageWidth}px` });

        //set board
        let hexagonTop, hexagonLeft, hexColumn = 0;

        //middle
        hexagonLeft = 0;
        for (var i = 1; i < 12; i++) {
            if ((hexColumn % 2) == 0) {
                hexagonTop += hexagonHeight;
            }            

            if (hexagonTop > stageHeight) {
                //move tile along one place
                hexagonLeft = (hexagonWidth / 4) * 3;

                //reset top
                hexagonTop = 0;

                //add column
                hexColumn += 1;
            }

            dungeon_crawler.main.drawHexagon(hexagonLeft, hexagonTop);

        }



        ////left
        //for (var i = 1; i < 10; i++) {
        //    hexagonTop = stageHeight - ((hexagonHeight / 2) + hexagonHeight * i);
        //    hexagonLeft = (stageWidth / 2) - (hexagonWidth + (hexagonWidth / 4));

        //    dungeon_crawler.main.drawHexagon(hexagonLeft, hexagonTop);
        //}

        ////right
        //for (var i = 1; i < 10; i++) {
        //    hexagonTop = stageHeight - ((hexagonHeight / 2) + hexagonHeight * i);
        //    hexagonLeft = ((stageWidth / 2) + (hexagonWidth / 4));

        //    dungeon_crawler.main.drawHexagon(hexagonLeft, hexagonTop);
        //}
    },

    drawHexagon(left, top) {
        $('#stage').append(`<div class="hexagon-tile" style="left: ${left}px; top: ${top}px"></div>`);
    }
};

window['dungeon_crawler_main_startup'] = dungeon_crawler.main.startup;
